const header=document.getElementById("header"),workTime=document.getElementById("workTime"),openMes=document.querySelector(".openMes"),closeMes=document.querySelector(".closeMes");function checkScrollAndHeader(){window.pageYOffset>500?header.classList.add("header_fixed"):header.classList.remove("header_fixed")}function showOpen(){openMes.classList.add("openMes_active"),setTimeout(()=>{openMes.classList.remove("openMes_active")},5e3)}function showExit(){closeMes.classList.add("closeMes_active"),setTimeout(()=>{closeMes.classList.remove("closeMes_active")},5e3)}document.addEventListener("DOMContentLoaded",checkScrollAndHeader),window.addEventListener("scroll",checkScrollAndHeader),workTime.addEventListener("mouseover",(function(){let e=new Date;e.getHours()>=9&&e.getHours()<18||18==e.getHours()&&0==e.getMinutes()?showOpen():showExit()}));